const { BlobServiceClient } = require("@azure/storage-blob");
const fs = require("fs");
const path = require("path");

// Read command line arguments
const connectionString = process.argv[2];
const containerName = process.argv[3];
const localFilePath = process.argv[4];

if (!connectionString || !containerName || !localFilePath) {
  console.error(
    "Usage: node uploadToAzureStorage.js <connection_string> <container_name> <local_file_path>"
  );
  process.exit(1);
}

async function main() {
  try {
    // Create a new BlobServiceClient
    const blobServiceClient = BlobServiceClient.fromConnectionString(connectionString);

    // Get a reference to the specified container
    const containerClient = blobServiceClient.getContainerClient(containerName);

    // Check if the container exists, and create it if not
    const containerExists = await containerClient.exists();
    if (!containerExists) {
      await containerClient.create();
      console.log(`Created container "${containerName}"`);
    }

    // Get a reference to the block blob client for uploading the file
    const blobName = path.basename(localFilePath);
    const blockBlobClient = containerClient.getBlockBlobClient(blobName);

    // Upload the local file to Azure Storage
    const data = fs.readFileSync(localFilePath);
    await blockBlobClient.upload(data, data.length);
    console.log(`Uploaded "${localFilePath}" to "${containerName}/${blobName}"`);
  } catch (error) {
    console.error("Error uploading file:", error.message);
  }
}

