import re
from collections import Counter

def tokenize(text):
    # Tokenize the text into words using regex and convert to lowercase
    return re.findall(r'\b\w+\b', text.lower())

def get_top_n_words(words, n=10):
    # Count the frequency of each word and return the top n most frequent words
    counter = Counter(words)
    return counter.most_common(n)

def main():
    file_path = input("Enter the path of the text file: ")

    with open(file_path, 'r') as f:
        text = f.read()

    words = tokenize(text)
    top_words = get_top_n_words(words)

    print("\nTop 10 most frequent words:")
    for i, (word, freq) in enumerate(top_words, start=1):
