		return
	}

	params := &s3.PutObjectInput{
		Bucket: aws.String(*awsBucket),
		Key:    aws.String(r.URL.Query().Get("filename")),
		Body:   bytes.NewReader(data),
	}

	_, err = svc.PutObject(params)
	if err != nil {
		http.Error(w, err.Error(), http.StatusInternalServerError)
		return
	}

	fmt.Fprintln(w, "File uploaded successfully")
}

func listFiles(w http.ResponseWriter, _ *http.Request) {
	resp, err := svc.ListObjectsV2(&s3.ListObjectsV2Input{Bucket: aws.String(*awsBucket)})
	if err != nil {
		http.Error(w, err.Error(), http.StatusInternalServerError)
		return
	}

	for _, item := range resp.Contents {
		fmt.Fprintln(w, *item.Key)
	}
}

func downloadFile(w http.ResponseWriter, r *http.Request) {
	key := mux.Vars(r)["key"]

	result, err := svc.GetObject(&s3.GetObjectInput{
		Bucket: aws.String(*awsBucket),
		Key:    aws.String(key),
	})

	if err != nil {
		http.Error(w, err.Error(), http.StatusInternalServerError)
		return
	}

	body, err := ioutil.ReadAll(result.Body)
	if err != nil {
		http.Error(w, err.Error(), http.StatusInternalServerError)
		return
	}

	w.Write(body)
}

func main() {
	r := mux.NewRouter()
	r.HandleFunc("/upload", uploadFile).Methods(http.MethodPost)
	r.HandleFunc("/list", listFiles).Methods(http.MethodGet)
	r.HandleFunc("/download/{key}", downloadFile).Methods(http.MethodGet)
	http.ListenAndServe(":8080", r)
}

