#include <iostream>
#include <cmath>

class Vector2D {
public:
    // Constructors
    Vector2D() : x(0), y(0) {}
    Vector2D(double x, double y) : x(x), y(y) {}

    // Destructor
    ~Vector2D() {}

    // Addition
    Vector2D operator+(const Vector2D& other) const {
        return Vector2D(x + other.x, y + other.y);
    }

    // Subtraction
    Vector2D operator-(const Vector2D& other) const {
        return Vector2D(x - other.x, y - other.y);
    }

    // Dot product
    double dot(const Vector2D& other) const {
        return x * other.x + y * other.y;
    }

    // Cross product (magnitude)
    double cross(const Vector2D& other) const {
        return x * other.y - y * other.x;
    }

    // Magnitude
    double magnitude() const {
        return std::sqrt(x * x + y * y);
    }

    // Normalization
    Vector2D normalize() const {
        double mag = magnitude();
        if (mag == 0) {
            return Vector2D(0, 0);
        }
        return Vector2D(x / mag, y / mag);
    }

    // Angle between vectors (in radians)
    double angleBetween(const Vector2D& other) const {
        double dotProduct = dot(other);
        double magnitudes = magnitude() * other.magnitude();
        if (magnitudes == 0) {
            return 0;
        }
        return std::acos(dotProduct / magnitudes);
    }

private:
    double x, y;
};

int main() {
    Vector2D v1(3, 4);
    Vector2D v2(1, 2);

    Vector2D sum = v1 + v2;
    std::cout << "Sum: (" << sum.x << ", " << sum.y << ")" << std::endl;

    Vector2D diff = v1 - v2;
    std::cout << "Difference: (" << diff.x << ", " << diff.y << ")" << std::endl;

    double dotProduct = v1.dot(v2);
    std::cout << "Dot product: " << dotProduct << std::endl;

    double crossProduct = v1.cross(v2);
    std::cout << "Cross product (magnitude): " << crossProduct << std::endl;

    double magnitude = v1.magnitude();
    std::cout << "Magnitude of v1: " << magnitude << std::endl;

    Vector2D normalized = v1.normalize();
    std::cout << "Normalized v1: (" << normalized.x << ", " << normalized.y << ")" << std::endl;

    double angle = v1.angleBetween(v2);
    std::cout << "Angle between v1 and v2 (in radians): " << angle << std::endl;

    return 0;
}

Here's a Java program that simulates a basic CPU scheduler using the Round Robin scheduling algorithm:

```java
import java.util.*;

class Process {
    int id, arrivalTime, burstTime, priority;
    int remainingBurstTime;

    public Process(int id, int arrivalTime, int burstTime, int priority) {
        this.id = id;
        this.arrivalTime = arrivalTime;
        this.burstTime = burstTime;
        this.priority = priority;
        this.remainingBurstTime = burstTime;
    }
}

public class RoundRobinScheduler {

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        System.out.print("Enter number of processes: ");
        int numProcesses = scanner.nextInt();

        List<Process> processList = new ArrayList<>();
        for (int i = 0; i < numProcesses; i++) {
            System.out.println("Enter details for process " + (i + 1));
            System.out.print("Arrival time: ");
            int arrivalTime = scanner.nextInt();
            System.out.print("Burst time: ");
            int burstTime = scanner.nextInt();
            System.out.print("Priority: ");
            int priority = scanner.nextInt();

            processList.add(new Process(i + 1, arrivalTime, burstTime, priority));
        }

        System.out.print("Enter time quantum: ");
        int timeQuantum = scanner.nextInt();

        roundRobinScheduling(processList, timeQuantum);
    }

    private static void roundRobinScheduling(List<Process> processList, int timeQuantum) {
        int currentTime = 0;
        int completedProcesses = 0;
        int totalWaitingTime = 0;
        int totalTurnaroundTime = 0;

        Queue<Process> readyQueue = new LinkedList<>();

        while (completedProcesses < processList.size()) {
            // Add arrived processes to the ready queue
            for (Process process : processList) {
                if (process.arrivalTime == currentTime && !readyQueue.contains(process)) {
                    readyQueue.add(process);
                }
            }

            // Execute the first process in the ready queue
            if (!readyQueue.isEmpty()) {
                Process currentProcess = readyQueue.poll();
                System.out.println("Executing process " + currentProcess.id);

                int timeSlice = Math.min(timeQuantum, currentProcess.remainingBurstTime);
                currentTime += timeSlice;
                currentProcess.remainingBurstTime -= timeSlice;

                // Check if the process is completed
                if (currentProcess.remainingBurstTime == 0) {
                    completedProcesses++;
                    totalWaitingTime += currentTime - currentProcess.arrivalTime - currentProcess.burstTime;
                    totalTurnaroundTime += currentTime - currentProcess.arrivalTime;
                } else {
                    // Add the process back to the ready queue
                    readyQueue.add(currentProcess);
                }
            } else {
                currentTime++;
            }
        }

        double averageWaitingTime = (double) totalWaitingTime / processList.size();
        double averageTurnaroundTime = (double) totalTurnaroundTime / processList.size();

        System.out.printf("Average waiting time: %.2f\n", averageWaitingTime);
        System.out.printf("Average turnaround time: %.2f\n", averageTurnaroundTime);
    }
}
```

This program accepts a list of processes with their arrival times, burst times, and priorities. It then simulates the Round Robin scheduling algorithm using a specified time quantum and outputs the order in which the processes are executed, along with the average waiting time and turnaround time.

Please note that this code does not handle any input validation or error handling, so make sure to provide valid inputs when running the program.

