#include <iostream>
#include <stack>
#include <string>
#include <sstream>

bool is_operator(const std::string &token) {
    return token == "+" || token == "-" || token == "*" || token == "/";
}

int perform_operation(const std::string &operation, int a, int b) {
    if (operation == "+") {
        return a + b;
    } else if (operation == "-") {
        return a - b;
    } else if (operation == "*") {
        return a * b;
    } else if (operation == "/") {
        return a / b;
    }
    throw std::runtime_error("Invalid operation");
}

int evaluate_expression(const std::string &expression) {
    std::istringstream iss(expression);
    std::stack<int> operands;

    std::string token;
    while (iss >> token) {
        if (is_operator(token)) {
            if (operands.size() < 2) {
                throw std::runtime_error("Not enough operands for operation");
            }

            int b = operands.top();
            operands.pop();
            int a = operands.top();
            operands.pop();

            int result = perform_operation(token, a, b);
            operands.push(result);
        } else {
            try {
                int operand = std::stoi(token);
                operands.push(operand);
            } catch (const std::invalid_argument &) {
                throw std::runtime_error("Invalid token in expression");
            }
        }
    }

    if (operands.size() != 1) {
        throw std::runtime_error("Too many operands in expression");
    }

    return operands.top();
}

int main() {
    std::string input;
    std::cout << "Enter an expression with space-separated tokens (e.g., \"3 4 +\"):" << std::endl;
    std::getline(std::cin, input);

    try {
        int result = evaluate_expression(input);
        std::cout << "Result: " << result << std::endl;
    } catch (const std::runtime_error &e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
```

