import sqlite3

def create_connection():
    conn = None
    try:
        conn = sqlite3.connect('books.db')
    except sqlite3.Error as e:
        print(e)
    return conn

def close_connection(conn):
    if conn:
        conn.close()

def create_table(conn):
    try:
        cursor = conn.cursor()
        cursor.execute('''CREATE TABLE IF NOT EXISTS books (
                            id INTEGER PRIMARY KEY,
                            title TEXT NOT NULL,
                            author TEXT NOT NULL,
                            publication_year INTEGER);''')
        conn.commit()
    except sqlite3.Error as e:
        print(e)

def add_book(conn, book):
    try:
        cursor = conn.cursor()
        cursor.execute("INSERT INTO books (title, author, publication_year) VALUES (?, ?, ?)", book)
        conn.commit()
    except sqlite3.Error as e:
        print(e)

def update_book(conn, book):
    try:
        cursor = conn.cursor()
        cursor.execute("UPDATE books SET title=?, author=?, publication_year=? WHERE id=?", book)
        conn.commit()
    except sqlite3.Error as e:
        print(e)

def delete_book(conn, book_id):
    try:
        cursor = conn.cursor()
        cursor.execute("DELETE FROM books WHERE id=?", (book_id,))
        conn.commit()
    except sqlite3.Error as e:
        print(e)

def list_books(conn):
    try:
        cursor = conn.cursor()
        cursor.execute("SELECT * FROM books")
        rows = cursor.fetchall()
        for row in rows:
            print(row)
    except sqlite3.Error as e:
        print(e)

