import re
from collections import Counter

def tokenize(text):
    # Tokenize the text into words using regex and convert to lowercase
    return re.findall(r'\b\w+\b', text.lower())

def get_top_n_words(words, n=10):
    # Count the frequency of each word and return the top n most frequent words
    counter = Counter(words)
    return counter.most_common(n)

def main():
    file_path = input("Enter the path of the text file: ")

    with open(file_path, 'r') as f:
        text = f.read()

    words = tokenize(text)
    top_words = get_top_n_words(words)

    print("\nTop 10 most frequent words:")
    for i, (word, freq) in enumerate(top_words, start=1):
        print(f"{i}. {word}: {freq}")

if __name__ == "__main__":
    main()

#include <boost/beast/core.hpp>
#include <boost/beast/websocket.hpp>
#include <boost/asio/connect.hpp>
#include <boost/asio/ip/tcp.hpp>
#include <cstdlib>
#include <iostream>
#include <string>
#include <thread>

namespace beast = boost::beast;
namespace http = beast::http;
namespace websocket = beast::websocket;
namespace net = boost::asio;
using tcp = net::ip::tcp;

int main(int argc, char* argv[]) {
    try {
        if (argc != 3) {
            std::cerr << "Usage: websocket-chat-client <host> <port>\n";
            return EXIT_FAILURE;
        }

        auto const host = argv[1];
        auto const port = argv[2];

        // The io_context is required for all I/O
        net::io_context ioc;

        // These objects perform our I/O
        tcp::resolver resolver{ioc};
        websocket::stream<tcp::socket> ws{ioc};

        // Look up the domain name
        auto const results = resolver.resolve(host, port);

        // Make the connection on the IP address we get from a lookup
        net::connect(ws.next_layer(), results.begin(), results.end());

        // Perform the WebSocket handshake
        ws.handshake(host, "/");

        // Start a separate thread to read messages from the server
        std::thread read_thread([&]() {
            beast::multi_buffer buffer;
            while (true) {
                try {
                    ws.read(buffer);
                    std::cout << "Received: " << beast::buffers_to_string(buffer.data()) << std::endl;
                    buffer.consume(buffer.size());
                } catch (const beast::system_error& se) {
                    if (se.code() != websocket::error::closed)
                        std::cerr << "Error: " << se.code().message() << std::endl;
                    break;
                }
            }
        });

        // Main thread for sending messages
        while (true) {
            std::string message;
            std::getline(std::cin, message);
            if (!message.empty()) {
                ws.write(net::buffer(message));
            }
        }

        // Close the WebSocket connection
        ws.close(websocket::close_code::normal);

        // Wait for the read thread to finish
        read_thread.join();

    } catch (std::exception const& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return EXIT_FAILURE;
    }

    return EXIT_SUCCESS;
}

