#include <iostream>
#include <vector>

// Function to print the Tic-Tac-Toe board
void print_board(const std::vector<std::vector<char>>& board) {
    for (const auto& row : board) {
        for (char cell : row) {
            std::cout << cell << " ";
        }
        std::cout << std::endl;
    }
}

// Function to check if there is a winner or a draw
bool check_winner(const std::vector<std::vector<char>>& board, char player) {
    // Check rows and columns
    for (int i = 0; i < 3; ++i) {
        if ((board[i][0] == player && board[i][1] == player && board[i][2] == player) ||
            (board[0][i] == player && board[1][i] == player && board[2][i] == player)) {
            return true;
        }
    }

    // Check diagonals
    if ((board[0][0] == player && board[1][1] == player && board[2][2] == player) ||
        (board[0][2] == player && board[1][1] == player && board[2][0] == player)) {
        return true;
    }

    return false;
}

// Function to check if the board is full (draw)
bool is_full(const std::vector<std::vector<char>>& board) {
    for (const auto& row : board) {
        for (char cell : row) {
            if (cell == '-') {
                return false;
            }
        }
    }
    return true;
}

int main() {
    std::vector<std::vector<char>> board(3, std::vector<char>(3, '-'));
    int row, col;
    bool player1_turn = true;

    while (!check_winner(board, 'X') && !check_winner(board, 'O') && !is_full(board)) {
        print_board(board);
        std::cout << "Player " << (player1_turn ? "1" : "2") << ", enter your move (row and column): ";
        std::cin >> row >> col;

        if (row >= 0 && row < 3 && col >= 0 && col < 3 && board[row][col] == '-') {
            board[row][col] = player1_turn ? 'X' : 'O';
            player1_turn = !player1_turn;
        } else {
            std::cout << "Invalid move. Try again." << std::endl;
        }
    }

    print_board(board);

    if (check_winner(board, 'X')) {
        std::cout << "Player 1 wins!" << std::endl;
    } else if (check_winner(board, 'O')) {
        std::cout << "Player 2 wins!" << std::endl;
    } else {
        std::cout << "It's a draw!" << std::endl;
    }

    return 0;
}
