from manim import Scene, Arrow, ArrowCircleFilledTip, Create, DOWN

class FilledCircleTipArrow(Scene):
    def construct(self):
        # Create an arrow using ArrowCircleFilledTip as the tip_shape
        arrow = Arrow(
            start=DOWN,
            end=DOWN*2,
            tip_shape=ArrowCircleFilledTip
        )

